struct Point1{
    int x;
    int y;
    int step;
};

class Solution {
public:
    int dx[4]={1,-1,0,0};
    int dy[4]={0,0,1,-1};
    int shortestBridge(vector<vector<int>>& A) {
        if (A.empty()||A[0].empty()) return 0;
        //将其中一个岛的数字由1->2 
        bool is_change=false;
        for (int i=0;i<A.size();i++){
            for (int j=0;j<A[0].size();j++){
                if (A[i][j]==1){
                    changeOneToTwo(A,i,j);
                    is_change=true;
                    break;
                }
            }
            if (is_change) break;
        }
        /*
        for (int i=0;i<A.size();i++){
            for (int j=0;j<A[0].size();j++){
                cout<<A[i][j]<<' ';
            }
            cout<<endl;
        }
        */
        int ans=__INT_MAX__;
        for (int i=0;i<A.size();i++){
            for (int j=0;j<A[0].size();j++){
                if (A[i][j]==2){
                    ans=min(ans,distance(A,i,j));
                }
            }

        }
        return ans-1;
    }

    int distance(vector<vector<int>>vec,int x,int y){
        queue<Point1> que;
        Point1 cur;
        cur.x=x,cur.y=y,cur.step=0;
        bool vis[vec.size()+1][vec[0].size()+1];
        memset(vis,0,sizeof vis);
        que.push(cur);

        while(!que.empty()){
            cur=que.front();
            que.pop();
            vis[cur.x][cur.y]=true;
            if (vec[cur.x][cur.y]==1) return cur.step;

            for (int i=0;i<4;i++){
                int xx=cur.x+dx[i];
                int yy=cur.y+dy[i];
                if (checkBound(xx,yy,vec.size(),vec[0].size())&&vec[xx][yy]==1) return cur.step+1;
                if (checkBound(xx,yy,vec.size(),vec[0].size()) && vis[xx][yy]==false && vec[xx][yy]==0){
                    vis[xx][yy]=true;
                    Point1 tmp;
                    tmp.x=xx,tmp.y=yy,tmp.step=cur.step+1;
                    que.push(tmp);
                }
            }
        }
        return 1000;
    }

    void changeOneToTwo(vector<vector<int>>&A,int x,int y){
       
        queue<Point1> que;
        Point1 cur;
        cur.x=x,cur.y=y,cur.step=0;
        que.push(cur);
        A[cur.x][cur.y]=2;

        while (!que.empty()){
            cur=que.front();
            que.pop();
            for (int i=0;i<4;i++){
                int xx=cur.x+dx[i];
                int yy=cur.y+dy[i];
                if (checkBound(xx,yy,A.size(),A[0].size()) &&A[xx][yy]==1){
                    A[xx][yy]=2;
                    Point1 tmp;
                    tmp.x=xx,tmp.y=yy,tmp.step=0;
                    que.push(tmp);
                }
            }
        }
    }
    bool checkBound(int x,int y,int m,int n){
        if (x<0||x>=m||y<0||y>=n) return false;
        return true;
    }
};

